/* ==UserStyle==
@name         nyan cat progress bar :D
@namespace    USO Archive
@author       keanuboom
@description  NYAN CAT !!!!
@version      20200921.09.19
@license      NONE
@preprocessor uso
==/UserStyle== */
@-moz-document domain("youtube.com")
{
  .html5-play-progress, .ytp-play-progress
  {
    background: url("data:image/png;base64,") repeat-x !important;
    background: linear-gradient(to bottom, #FF0000 0%, #FF0000 16.5%, #FF9900 16.5%, #FF9900 33%, #FFFF00 33%, #FFFF00 50%, #33FF00 50%, #33FF00 66%, #0099FF 66%, #0099FF 83.5%, #6633ff 83.5%, #6633ff 100%) !important;
    background: -webkit-linear-gradient(top, #FF0000 0%, #FF0000 16.5%, #FF9900 16.5%, #FF9900 33%, #FFFF00 33%, #FFFF00 50%, #33FF00 50%, #33FF00 66%, #0099FF 66%, #0099FF 83.5%, #6633ff 83.5%, #6633ff 100%) !important;
    background: -moz-linear-gradient(top, #FF0000 0%, #FF0000 16.5%, #FF9900 16.5%, #FF9900 33%, #FFFF00 33%, #FFFF00 50%, #33FF00 50%, #33FF00 66%, #0099FF 66%, #0099FF 83.5%, #6633ff 83.5%, #6633ff 100%) !important;
  }

  .html5-load-progress, .ytp-load-progress
  {
    background: url !important;
  }

  .html5-scrubber-button, .ytp-scrubber-button
  {
    background: url("data:image/gif;base64,") !important;
    width: 34px !important;
    height: 21px !important;
    border: none !important;
    margin-left: -18px !important;
    margin-top: 0px !important;
   /*[[show_nyan]]*/;
  }

  .ytp-progress-bar-container:hover .ytp-scrubber-button,
.ytp-progress-bar-container:hover .ytp-load-progress
  {
    image-rendering: pixelated;
  }

  .html5-progress-bar-container, .ytp-progress-bar-container
  {
    height: 12px !important;
  }

  .html5-progress-bar, .ytp-progress-bar
  {
    margin-top: 12px !important;
  }

  .html5-progress-list, .ytp-progress-list, .video-ads .html5-progress-list.html5-ad-progress-list, .video-ads .ytp-progress-list.ytp-ad-progress-list
  {
    height: 12px !important;
  }

  .ytp-volume-slider-track
  {
    background: #0C4177 !important;
  }
}

/* ==UserStyle==
@name          Clean YouTube
@author        0ko (https://userstyles.world/~0ko)
@description   Simplificator and debloater for YouTube aimed at minimizing breaking. Formerly known as Enhancements for YouTube.
@version       3.4.1
@license       MIT
@preprocessor  stylus

@namespace     0ko userstyles
@homepageURL   https://codeberg.org/0ko/UserStyles#clean-youtube
@supportURL    https://codeberg.org/0ko/UserStyles/issues

@var checkbox  noShorts               "No short videos"               1
@var checkbox  noVoiceSearch          "No voice search"               1
@var checkbox  noUpload               "No upload button"              0
@var checkbox  lessCrazyAnimations    "Less crazy button animations"  1
@var checkbox  noMemberships          "No channel memberships"        1

@var select    noHome_                "Home:"                 {" ":"_"}
@var checkbox  noHomeTags             "  No top tags"                 0
@var checkbox  noHomeDifferent        "  No New to you"               1
@var checkbox  noHomeLatestPosts      "  No Latest posts"             0
@var checkbox  noHomeVideoSurveys     "  No video Surveys"            1
@var checkbox  noHomeJunkSections     "  No other junk sections"      1

@var select    noVideo_               "Video:"                {" ":"_"}
@var checkbox  noVideoCornerLogo      "  No logo in corner"           1
@var checkbox  noVideoPlayNext        "  No Next video button"        1
@var checkbox  noVideoClipButton      "  No Clip button"              1
@var checkbox  noVideoWatchNext       "  No Watch next column (emptiness) OR new bottom grid" 0
@var checkbox  noVideoEndscreenVideos "  No endscreen video suggestions" 0
@var checkbox  noVideoSuggestionsTags "  No tags for Watch next"      1
@var checkbox  noVideoVignette        "  No bottom vignette"          0
@var checkbox  noVideoVignetteTop     "  No top vignette (fullscreen)" 0
@var checkbox  noVideoChannelLinks    "  No channel links (socials) in description" 1
@var checkbox  noVideoFundraising     "  No fundraising"              1

@var select    noLogo_                "Logo:"                 {" ":"_"}
@var checkbox  noLogoAccent           "  Monochrome button"           0
@var checkbox  noLogoCountryCode      "  No country code"             1

@var select    noSidebar_             "Sidebar:"              {" ":"_"}
@var checkbox  noSidebarNewnessDots   "  No newness dots"             1
@var checkbox  noSidebarBloat         "  No bloat"                    1

@var checkbox  noSubsTopGap           "Subscriptions: reduce top gap" 1

@var checkbox  noSearchUnrelated      "Search: no unrelated videos"   1

@var checkbox  noCommentsPaidBg       "Comments: no paid background"  1

==/UserStyle== */

imp = !important

@-moz-document domain("youtube.com")

	if noShorts
		/* on Main and in Subscriptions (alt: &:has(a[href^="/feed/subscriptions/shorts"])) */
		ytd-rich-shelf-renderer[is-shorts]
			display: none imp

		/* in Search */
		ytd-reel-shelf-renderer /* section */
			&:has(ytm-shorts-lockup-view-model-v2)
				display: none imp
		ytd-video-renderer /* video */
			&:has(a#video-title[href^="/shorts/"])
				display: none imp

		/* "Shorts remixing this video" */
		#description ytd-reel-shelf-renderer.ytd-structured-description-content-renderer:has(#dismissible)
			display: none imp

	if noVoiceSearch
		#voice-search-button.ytd-masthead
			display: none imp

	if noUpload
		#masthead #end #buttons button:has(path[d$="1h8V3h1v8h8v1z"])
			display: none imp

	if lessCrazyAnimations
		ytd-segmented-like-dislike-button-renderer
		segmented-like-dislike-button-view-model
			.smartimation__border-gradient
				display: none
			.smartimation--active-border .smartimation__background
				opacity: 0
			ytd-lottie-player
				filter: saturate(0)

		#subscribe-button
			.animated-action__background-container
			.animated-action__lottie
			.smartimation__border
				display: none
			ytd-subscribe-button-renderer[animated-action-enabled][subscribed]
				transition: width .1s ease

		/* Remove seekbar gradient */
		.ytp-play-progress
			background: rgb(255, 0, 51) imp

	if noMemberships
		ytd-item-section-renderer
			ytd-recognition-shelf-renderer
				display: none

	/* Homepage */

	if noHomeTags
		ytd-feed-filter-chip-bar-renderer
			display: none imp
		ytd-rich-grid-renderer #contents.ytd-rich-grid-renderer
			padding-top: 16px imp


	if noHomeDifferent
		.ytd-feed-nudge-renderer /* "Looking for something different?" */
		yt-chip-cloud-chip-renderer[chip-style="STYLE_REFRESH_TO_NOVEL_CHIP"]
			display: none imp

	if noHomeLatestPosts
		#dismissible
		.ytd-rich-shelf-renderer
		.ytd-rich-section-renderer
			&:has(ytd-post-renderer)
				display: none imp

	if noHomeVideoSurveys
		#dismissible .ytd-inline-survey-renderer
		ytd-popup-container ytd-checkbox-survey-renderer
			display: none imp

	if noHomeJunkSections
		.ytd-rich-shelf-renderer:has(a[href="/playables"])
		.ytd-rich-shelf-renderer:has(a[href="/feed/storefront"]) /* "Primetime movies for you" */
			display: none imp


	/* Video */

	if noVideoCornerLogo
		.iv-branding
		/*branding-img-container ytp-button*/
		/*branding-img iv-click-target     */
		/*branding-context-container-outer */
			display: none imp

	if noVideoPlayNext
		#ytd-player .ytp-next-button
			display: none imp

	if noVideoClipButton
		yt-button-view-model:has(path[d*="1-1 1 .45 1 1zm-1 9c-.55 0-1 .45-1 1s.45 1 1 1 1-.45 1-1-.45-1-1-1zm3.79-7.77L21"])
			display: none imp

	if noVideoWatchNext
		ytd-watch-next-secondary-results-renderer
		ytd-watch-grid.ytd-page-manager #bottom-grid
			display: none imp

	if noVideoEndscreenVideos
		#movie_player
			.ytp-ce-channel
			.ytp-ce-video
				display: none imp
		.ytp-endscreen-content
			display: none imp

	if noVideoSuggestionsTags
		ytd-watch-next-secondary-results-renderer
			yt-related-chip-cloud-renderer.&
				display: none imp
			ytd-watch-flexy[default-layout] & ytd-compact-video-renderer:first-child
				margin-top: 0 imp

	if noVideoVignette
		.ytp-gradient-bottom
			display: none imp

	if noVideoVignetteTop
		.ytp-gradient-top
			display: none imp

	if noVideoChannelLinks
		ytd-video-description-infocards-section-renderer yt-button-view-model
			/* Patreon, Instagram, Twitter, Discord, Reddit */
			&:has(a[href^="https://"])
				display: none imp

	if noVideoFundraising
		#donation-shelf
		ytd-badge-supported-renderer.ytd-watch-metadata
			display: none imp


	/* Logo */

	if noLogoAccent
		#logo svg g:first-child:not(g g)
				filter: grayscale(100%) contrast(1.25)
		[dark] #logo svg g:first-child:not(g g)
				filter: grayscale(100%) invert(100%) brightness(1.33)

	if noLogoCountryCode
		/* Kind of pointless country code, since the content you see and watch doesn't necessarily depend on it. Imagine every website you visit having a country code in it's logo. */
		#logo
		ytd-topbar-logo-renderer
			#country-code
				display: none


	/* Sidebar */

	if noSidebarNewnessDots
		#newness-dot
			display: none imp

	if noSidebarBloat
		.tp-yt-app-drawer
			/* 1st block */
			a#endpoint[title="Shorts"]
			/* 2nd block */
			a[href^="/feed/clips"][title]
			/* Explore */
			a[href^="/feed/trending"][title]
			a[href^="/channel/UC-9-kyTW8ZkZNDHQJ6FgpwQ"][title]
			a[href^="/feed/storefront"][title]
			a[href^="/channel/UC4R8DWoMoI7CAwX8_LjQHig"][title]
			a[href^="/gaming"][title]
			a[href^="/channel/UCEgdi0XIXXZ-qJOFPf4JSKw"][title]
			/* More from YouTube */
			a[href^="https://studio.youtube.com"][title="Creator Studio"]
			a[href^="https://studio.youtube.com"][title="YouTube Studio"]
			a[href^="https://music.youtube.com"][title="YouTube Music"]
			a[href^="https://www.youtubekids.com"][title="YouTube Kids"]
			a[href^="https://tv.youtube.com"][title="YouTube TV"]
			/* Last block */
			a#endpoint[title="Send feedback"]
			/* Footer */
			#guide-links-secondary.ytd-guide-renderer > a[href="/new"] /* "Test new features" - premium only */
			#footer.ytd-guide-renderer #copyright
				display: none imp

			#sections
				ytd-guide-section-renderer:nth-last-child(2)
				ytd-guide-section-renderer:nth-last-child(3)
					display: none


		/* Subscriptions */

		if noSubsTopGap
			ytd-browse[page-subtype="subscriptions"] .grid-subheader.ytd-shelf-renderer
				margin-top: 0 imp


		/* Search */

		if noSearchUnrelated
			.ytd-search ytd-shelf-renderer
				display: none imp


		/* Comments */

		if noCommentsPaidBg
			#paid-comment-background
				display: none imp

/* ==UserStyle==
@name         Youtube.com - Square corners
@version      20240219.20.10
@namespace    https://userstyles.world/user/T0biasCZe
@description  Removes almost all the round corners of elements and replaces them with sharp corners
@author       T0biasCZe
==/UserStyle== */

@-moz-document domain("youtube.com") {
/*very round ads*/
#media-container.ytd-display-ad-renderer{
    border-radius: 1000px !important;    
}
/*remove YT shorts button*/
#endpoint[title="Shorts"]{
    display: none !important;
}

/*video*/
ytd-watch-flexy[rounded-player-large][default-layout] #ytd-player.ytd-watch-flexy{
    border-radius: 0 !important;
}
.ytp-settings-menu{
    border-radius: 0 !important;
}

/*under video*/
#avatar.ytd-video-owner-renderer{
    border-radius: 0 !important;
}
#description.ytd-watch-metadata{
    border-radius: 0 !important;    
}
#header.ytd-video-description-infocards-section-renderer #thumbnail.ytd-video-description-infocards-section-renderer{
    border-radius: 0 !important;
}

/*subscribe and like buttons*/
.yt-spec-touch-feedback-shape,
.yt-spec-button-shape-next--size-m{
    border-radius: 0 !important;
}
.yt-spec-button-shape-next--size-m.yt-spec-button-shape-next--segmented-start, .yt-spec-button-shape-next--size-m.yt-spec-button-shape-next--segmented-end{
    border-radius: 0 !important;
}

/*enhancer for youtube panel*/
.efyt-control-bar{
    border-radius: 0 !important;
}
/*paylist in description*/
#thumbnail.ytd-structured-description-playlist-lockup-renderer, .collections-stack-wiz__collection-stack1--small{
    border-radius: 0 !important;
}
/*game description in description*/
ytd-rich-metadata-renderer[rounded]{
    border-radius: 0 !important;
}



/*video thumbnails*/
ytd-thumbnail[size="medium"] a.ytd-thumbnail, ytd-thumbnail[size="medium"]::before, ytd-thumbnail, ytd-thumbnail[size="large"] a.ytd-thumbnail, ytd-thumbnail[size="large"]::before, .rich-thumbnail.ytd-ghost-grid-renderer{
    border-radius: 0 !important;
}
ytd-menu-popup-renderer{
    border-radius: 0 !important;
}
yt-chip-cloud-chip-renderer{
    border-radius: 0 !important;
}
#avatar.ytd-rich-grid-media, .channel-avatar.ytd-ghost-grid-renderer{
    border-radius: 0 !important;
}
.text-shell.ytd-ghost-grid-renderer{
    border-radius: 0 !important;
}
/*playlist thumbnail in sidebar*/
.collections-stack-wiz__collection-stack1--medium, ytd-playlist-thumbnail[size="medium"] a.ytd-playlist-thumbnail, ytd-playlist-thumbnail[size="medium"]::before{
    border-radius: 0 !important;
}
/*playlist video count*/
ytd-thumbnail-overlay-bottom-panel-renderer[use-modern-collections-v2]{
    border-radius: 0 !important;
}
ytd-playlist-panel-renderer[modern-panels]:not([within-miniplayer]) #container.ytd-playlist-panel-renderer{
    border-radius: 0 !important;
}


/*comment section*/
#author-thumbnail.ytd-comment-renderer yt-img-shadow.ytd-comment-renderer,
#author-thumbnail.ytd-comment-simplebox-renderer{
    border-radius: 0 !important;
}

/*search bar*/
#container.ytd-searchbox, #search-icon-legacy.ytd-searchbox{
    border-radius: 0 !important;
}
yt-img-shadow.ytd-topbar-menu-button-renderer{
    border-radius: 0 !important;
}
#voice-search-button.ytd-masthead{ /*mic search*/
    border-radius: 0 !important;
}


/*clip creator menu*/
ytd-engagement-panel-section-list-renderer[modern-panels]:not([live-chat-engagement-panel]){
    border-radius: 0 !important;
}
/*left side panel*/
yt-img-shadow.ytd-guide-entry-renderer, ytd-guide-entry-renderer{
    border-radius: 0 !important;
}
#endpoint.yt-simple-endpoint.ytd-guide-entry-renderer:hover, #endpoint.yt-simple-endpoint.ytd-guide-entry-renderer:focus{
    border-radius: 0 !important;
}



/*channel page*/
ytd-c4-tabbed-header-renderer[use-page-header-style] .page-header-banner.ytd-c4-tabbed-header-renderer{
    border-radius: 0 !important;
}
#avatar.ytd-c4-tabbed-header-renderer{
    border-radius: 0 !important;
}
#avatar{
    border-radius: 0 !important;
}
}

/* ==UserStyle==
@name           More Pride Tags
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    A new userstyle
@author         Me
==/UserStyle== */

@-moz-document domain("www.tumblr.com") {

    /* HOW TO ADD MORE */
    /* 

    1. copy one of the chunks of code below
    2. change the aria-label to the tag name
    3. change the colours! add more if you need by adding a comma and then another colour
    4. done !!

    */

    [aria-label="aroace"]{
        background-image: linear-gradient(90deg,#ef9007,#f6d317, #ffffff,#45bcee, #1e3f54);
        background-clip: text;
        color: transparent;
    }

    [aria-label="genderfluid"]{
        background-image: linear-gradient(90deg,#FF76A4,#fff, #c011d7,#000, #2F3CBE);
        background-clip: text;
        color: transparent;
    }

    [aria-label="agender"]{
        background-image: linear-gradient(90deg,#000,#BCC4C7, #FFFFFF, #B7F684, #FFFFFF,#BCC4C7, #000);
        background-clip: text;
        color: transparent;
    }

    [aria-label="omnisexual"]{
        background-image: linear-gradient(90deg,#fe9ace,#ff53bf, #200044, #6760fe, #8ea6ff);
        background-clip: text;
        color: transparent;
    }

    [aria-label="abrosexual"]{
        background-image: linear-gradient(90deg,#75ca91,#75ca91, #ffffff, #e695b5, #d9446c);
        background-clip: text;
        color: transparent;
    }

    [aria-label="genderqueer"]{
        background-image: linear-gradient(90deg,#b57edc, #ffffff, #4a8123);
        background-clip: text;
        color: transparent;
    }

    [aria-label="demiboy"]{
        background-image: linear-gradient(90deg,#7f7f7f,#C4C4C4,#9DD7EA,#ffffff,#9DD7EA,#C4C4C4, #7f7f7f);
        background-clip: text;
        color: rgba(0, 0, 0, 0);
    }


    [aria-label="demigirl"]{
        background-image: linear-gradient(90deg,#7f7f7f,#C4C4C4,#fdadc8,#ffffff,#fdadc8,#C4C4C4, #7f7f7f);
        background-clip: text;
        color: transparent;
    }

    [aria-label="demigender"]{
        background-image: linear-gradient(90deg,#7f7f7f,#C4C4C4,#fffb8c,#ffffff,#fffb8c,#C4C4C4, #7f7f7f);
        background-clip: text;
        color: transparent;
    }













}

/* ==UserStyle==
@name         3 rows Playlist Watch later Youtube
@version      20240121.05.11
@namespace    ?
==/UserStyle== */

@-moz-document url-prefix("https://www.youtube.com/playlist") {
#contents > ytd-playlist-video-list-renderer > #contents{
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    width: 100%;
    gap: 0.4rem;
}

@media only screen and (min-aspect-ratio: 1 / 1) {
#contents > ytd-playlist-video-renderer {
    width: 49%;
}
}
@media only screen and (min-aspect-ratio: 1.8 / 1) {
#contents > ytd-playlist-video-renderer {
    width: 32%;
}
}


ytd-section-list-renderer:not([hide-bottom-separator]):not([page-subtype="history"]):not([page-subtype="memberships-and-purchases"]):not([page-subtype="ypc-offers"]):not([live-chat-engagement-panel]) #contents.ytd-section-list-renderer > .ytd-section-list-renderer:not(:last-child):not(ytd-page-introduction-renderer):not([item-dismissed]):not([has-destination-shelf-renderer]):not(ytd-minor-moment-header-renderer):not([has-section-group-view-model]) {
    border-bottom: 0px hidden;
}
}

/* ==UserStyle==
@name         Remove The Shadow Gradient on YouTube Video Player
@namespace    USO Archive
@author       Joseph Dickson
@description  Removes the shadow gradient behind the YouTube video player bar and gradient above. Helps a little bit with reading and seeing stuff normally hidden by this bar. :P I miss the old player that didn't cover videos :'/  I've made the option to set the opacity between 0% and 100% to vary how visible the gradients are. hope you enjoy~ ^_^  I'll try find out if I can give the option to color them, I'm still learning. let me know in the comments if you want this feature, cheers. :D    -If you want the Player bar to be moved below the video, try my other style for it: style 137651
@version      20161201.04.21
@license      CC0-1.0
@preprocessor uso
@advanced dropdown GTOP "Set Opacity of Top Gradient (when full screen)" {
    0_ "0% (hidden)*" <<<EOT 0 EOT;
    10_ "10%" <<<EOT 0.1 EOT;
    20_ "20%" <<<EOT 0.2 EOT;
    30_ "30%" <<<EOT 0.3 EOT;
    40_ "40%" <<<EOT 0.4 EOT;
    50_ "50%" <<<EOT 0.5 EOT;
    60_ "60%" <<<EOT 0.6 EOT;
    70_ "70%" <<<EOT 0.7 EOT;
    80_ "80%" <<<EOT 0.8 EOT;
    90_ "90%" <<<EOT 0.9 EOT;
    100_ "100% (unaffected)" <<<EOT 1 EOT;

}
@advanced dropdown GBOTTOM "Set Opacity of Bottom Gradient (behind player)" {
    0_ "0% (hidden)*" <<<EOT 0 EOT;
    10_ "10%" <<<EOT 0.1 EOT;
    20_ "20%" <<<EOT 0.2 EOT;
    30_ "30%" <<<EOT 0.3 EOT;
    40_ "40%" <<<EOT 0.4 EOT;
    50_ "50%" <<<EOT 0.5 EOT;
    60_ "60%" <<<EOT 0.6 EOT;
    70_ "70%" <<<EOT 0.7 EOT;
    80_ "80%" <<<EOT 0.8 EOT;
    90_ "90%" <<<EOT 0.9 EOT;
    100_ "100% (unaffected)" <<<EOT 1 EOT;

}
==/UserStyle== */
@-moz-document url-prefix("https://www.youtube.com/") {
/* Remove Shadow on YouTube Video Player Bar*/
#movie_player > div.ytp-gradient-bottom {opacity: /*[[GBOTTOM]]*/ !important;}
#movie_player > div.ytp-gradient-top {opacity: /*[[GTOP]]*/ !important;}
/*set opacity between 0 and 1 to set how visible the gradients are*/
}

/* ==UserStyle==
@name YouTube Catppuccin
@namespace github.com/catppuccin/userstyles/styles/youtube
@homepageURL https://github.com/catppuccin/userstyles/tree/main/styles/youtube
@version 2025.01.05
@supportURL https://github.com/catppuccin/userstyles/issues?q=is%3Aopen+is%3Aissue+label%3Ayoutube
@description Soothing pastel theme for YouTube
@author Catppuccin
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve*", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]

@var checkbox logo "Enable YouTube logo" 1
@var checkbox oled "Enable black bars" 0
==/UserStyle== */

@-moz-document domain("youtube.com") {
  :root[dark] {
    #catppuccin(@darkFlavor);
  }
  :root:not([dark]) {
    #catppuccin(@lightFlavor);
  }

  #catppuccin(@flavor) {
    @rosewater: @catppuccin[@@flavor][@rosewater];
    @flamingo: @catppuccin[@@flavor][@flamingo];
    @pink: @catppuccin[@@flavor][@pink];
    @mauve: @catppuccin[@@flavor][@mauve];
    @red: @catppuccin[@@flavor][@red];
    @maroon: @catppuccin[@@flavor][@maroon];
    @peach: @catppuccin[@@flavor][@peach];
    @yellow: @catppuccin[@@flavor][@yellow];
