/* ==UserStyle==
@name         ✨ Picrew Desktop
@version      20220115.00.54
@namespace    userstyles.world/user/etstringy
@description  ✨ Picrew Desktop CSS
Expands the image editor to use the full screen instead of the regular phone UI.
@author       etstringy
@license      No License
==/UserStyle== */

@-moz-document domain("picrew.me") {
.play-Imagemaker {
    flex-direction: row;
    width: 100%;
    height: 200vh !important;
}

.play-Container_Imagemaker {
    flex: none !important;
}

.play-Imagemaker_Footer,
.play-Container_Right,
.play-Container_Left {
    display: none;
}

.imagemaker_control_wrapper {
    position: relative;
    flex: 1;
    display: flex;
    flex-direction: column;
}

.imagemaker_canvas_wrapper {
    width: 50%;
    height: 100% !important;
}

.imagemaker_itemlist ul li {
    width: 15.6%;
}

.imagemaker_colorpalette {
    display: block;
}
}

/* ==UserStyle==
@name         Dark Wikipedia Rounded
@namespace    USO Archive
@author       jojobyte
@description  Modified version of tjwoosta Wikipedia Dark grey Smooth.
@version      20110426.14.06
@license      NONE
@preprocessor uso
==/UserStyle== */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(wikipedia.org)

{

* {
background: #262626 !important;
border-color: #262626 !important;
color: #9a9a9a !important;
}

#p-logo a {
background-position:center center !important;
background-repeat:no-repeat !important;
}


a{
color: #000000 !important;
}

a:visited{
color: #ccc !important;
}

a:link{
color: #ccc !important;
font-weight: bold !important;
}

a:hover, a:hover span {
color: #fff !important;
text-decoration: underline !important;
}

#content, #content * {
background: #202020 !important;
border-color: #555 !important;
}

#content {
-moz-border-radius: 10px;
-webkit-border-radius: 10px;
border-radius: 10px;
}

#toc, #toc * {
background: #262626 !important;
border-color: #262626 !important;
}

#toc {
-moz-border-radius: 10px;
-webkit-border-radius: 10px;
border-radius: 10px;
}

#p-cactions li.selected a, #p-cactions li a, #p-cactions li a:hover {
